INSERT INTO `wp_showbiz_templates` (`id`, `title`, `type`, `content`, `css`, `ordering`, `params`) VALUES
(1, 'Grey Skin', 'item', '<!-- THE MEDIA HOLDER PART -->\n<div class="mediaholder">\n	<div class="mediaholder_innerwrap">\n		<img alt="image" src="[showbiz_image]">\n\n		<!-- THE HOVER EFFECT (SPECIAL CLASS  - hovercover) -->\n        <div class="hovercover">\n\n			<!-- LINK TO THE POST -->\n			<a href="[showbiz_link]">\n				<div class="linkicon notalone"><i class="sb-icon-link"></i></div>\n			</a>\n			<!-- LIGHTBOX LINK -->\n			<a class="fancybox" rel="group" href="[showbiz_image]"><div class="lupeicon notalone"><i class="sb-icon-search"></i></div></a>\n		</div>	<!-- END OF THE HOVER EFFECT -->\n      </div>\n</div><!-- END OF MEDIA HOLDER PART -->\n\n<!-- VISIBLE TEXTS ON THE TEASER - TITLE AND EXCERPT -->\n<div class="detailholder">\n	<div class="showbiz-title"><a href="[showbiz_link]">[showbiz_title]</a></div>\n	<div class="showbiz-description">[showbiz_excerpt]</div>\n</div>', '/********************************************\n\n	STYLE SETTINGS FOR THE GREY SKIN\n\n*********************************************/\n\n[itemid].showbiz-container{\n	max-width:1210px;\n	min-width:300px;\n}\n\n[itemid] .showbiz-title				{\n                                        margin-top:10px;\n                                        text-align:center;\n                                    }\n\n[itemid] .showbiz-title,\n[itemid] .showbiz-title a,\n[itemid] .showbiz-title a:visited,\n[itemid] .showbiz-title a:hover		{\n                                            color:#555;\n                                            font-family: ''Open Sans'', sans-serif;\n                                            font-size:14px;\n                                            text-transform:uppercase;\n                                            text-decoration: none;\n                                            font-weight:700;\n                                    }\n\n[itemid] .showbiz-description		{\n                                        margin-top:10px;\n                                        text-align:center;\n                                        font-size:13px;\n                                        line-height:22px;\n                                        color:#777;\n                                        font-family: ''Open Sans'', sans-serif;\n                                    }\n\n\n\n[itemid]  .mediaholder 				{	background-color:#d5d5d5;\n                                        border:1px solid #cecece;\n                                        border-radius:2px;-moz-border-radius:2px;-webkit-border-radius:2px;\n                                        padding:7px;\n                                    }\n\n[itemid]  .hovercover				{	background:rgba(0,0,0,0.5);  }\n\n[itemid] li:hover .mediaholder img  {	filter: url("data:image/svg+xml;utf8,<svg xmlns=\\''http://www.w3.org/2000/svg\\''><filter id=\\''grayscale\\''><feColorMatrix type=\\''matrix\\'' values=\\''0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\\''/></filter></svg>#grayscale");\n                                        filter: gray; /* IE6-9 */\n                                        -webkit-filter: grayscale(100%);\n                                    }', 1, ''),
(2, 'Light Skin', 'item', '<!-- THE MEDIA HOLDER -->\n<div class="mediaholder">\n	<div class="mediaholder_innerwrap">\n		<img alt="" src="[showbiz_image]">\n\n        <!-- HOVER EFFECTS , hovercover CLASS IS PREDEFINED CLASS -->\n		<div class="hovercover">\n			<!-- LINK TO POST -->\n			<a href="[showbiz_link]">\n				<div class="linkicon notalone"><i class="sb-icon-link"></i></div>\n			</a>\n			<!-- LIGHTBOX FANCYBOX WITH THE IMAGE -->\n			<a class="fancybox" rel="group" href="[showbiz_image]"><div class="lupeicon notalone"><i class="sb-icon-search"></i></div></a>\n		</div>\n	</div>\n</div>\n<!-- END OF MEDIA HOLDER HERE -->\n\n\n<!-- THE VISIBLE CONTENT HOLDER ON THE TEASER-->\n<div class="detailholder">\n\n		<h4 class="showbiz-title txt-center"><a href="[showbiz_link]">[showbiz_title]</a></h4>\n		<div class="divide5"></div>\n		<p class="txt-center">[showbiz_excerpt]</p>\n</div>', '/**************************************\n\n	STYLE SETTINGS FOR LIGHT SKIN\n\n***************************************/\n\n[itemid].showbiz-container	{\n                            	max-width:1210px;\n                              	min-width:300px;\n                         	 }\n\n[itemid] .showbiz-title		{\n                              	margin-top:10px;\n                                text-align:center;\n                            }\n\n[itemid] .showbiz-title,\n[itemid] .showbiz-title a,\n[itemid] .showbiz-title a:visited,\n[itemid] .showbiz-title a:hover	{\n                                color:#555;\n                                font-family: ''Open Sans'', sans-serif;\n                                font-size:14px;\n                                text-transform:uppercase;\n                                text-decoration: none;\n                                font-weight:700;\n                                        margin-bottom:0px !important;\n                        }\n\n[itemid] .showbiz-description{\n                                margin-top:10px;\n                                text-align:center;\n                                font-size:13px;\n                                line-height:22px;\n                                color:#777;\n                                font-family: ''Open Sans'', sans-serif;\n                            }\n\n[itemid]  ul li {\n                                margin-right:20px;                              \n\n                          }\n\n\n[itemid]  .detailholder	{\n                                background-color:#f5f5f5;\n                                padding:10px 20px 10px;\n                                border-radius:0px 0px 6px 6px;\n                                -moz-border-radius:0px 0px 6px 6px;\n                                -webkit-border-radius:0px 0px 6px 6px;\n								border-left: 1px solid #e8e8e8;\n								border-right: 1px solid #e8e8e8;\n								border-bottom: 1px solid #e8e8e8;\n                            }\n\n[itemid]  .mediaholder_innerwrap  {\n                                border-radius:6px 6px 0px 0px;\n                                -moz-border-radius:6px 6px 0px 0px;\n                                -webkit-border-radius:6px 6px 0px 0px;\n                                }\n\n[itemid]  .hovercover	{\n                                background:rgba(0,168,255,0.75);\n                                border-radius:6px 6px 0px 0px;\n                                -moz-border-radius:6px 6px 0px 0px;\n                                -webkit-border-radius:6px 6px 0px 0px;\n                      }\n\n[itemid]  .reveal_wrapper	{\n                                border-radius:6px;\n                                -moz-border-radius:6px;\n                                -webkit-border-radius:6px;\n                                }', 2, ''),
(3, 'Caption Skin', 'item', '<!-- SPECIAL CLASSES:\ngo-to-top -> Moves Caption to the Top Position after Hover,\nreveal_wrapper -> The Hidden Content which is shown after click on openme,\nopenme, closeme -> The open , close icon in for show/hide the Hidden Content,\nreveal_opener -> The Wrapper for the open/close Buttons,\nshow_on_hover -> Show the reveal_opener only if mouse is hovering the teaser\nhas been used. See Documentation for More Info\n-->\n\n	<!-- THE MEDIA HOLDER -->\n	<div class="mediaholder">\n		<div class="mediaholder_innerwrap">\n			<img alt="" src="[showbiz_image]">\n		</div>\n	</div><!-- END OF MEDIA HOLDER -->\n\n\n	<!-- ANIMATED HEADING INFORMATION, ALWAYS VISIBLE -->\n	<h4 class="showbiz-title go-to-top"><div class="[showbiz_color]"><a href="[showbiz_link]">[showbiz_title]</a></div></h4>\n\n	<!-- REVEAL CONTAINER (SINGLE MODE) -->\n	<div class="reveal_container">\n\n		<!-- THE REVEAL CONTENT, ONLY IN DETAIL MODE VISIBLE -->\n		<div class="reveal_wrapper">\n			<p class="pt40">[showbiz_excerpt]</p>\n		</div>\n\n	</div><!-- END OF REVEAL CONTAINER -->\n\n\n	<!-- THE REVEAL OPEN/CLOSE BUTTON - ONLY VISIBLE ON HOVER, DEFAULT STYLE -->\n	<a class="reveal_opener show_on_hover">\n		<span class="openme">+</span>\n		<span class="closeme">-</span>\n	</a>', '/**************************************\n\n	STYLE SETTINGS FOR CAPTION SKIN\n\n***************************************/\n\n\n[itemid] .mediaholder 							{	background-color:#fff;border:none;padding:5px;\n													border-radius:2px;-moz-border-radius:2px;-webkit-border-radius:2px;\n												}\n[itemid] .showbiz-title.go-to-top              	{   margin-top:-50px;}\n[itemid] .revactive .showbiz-title.go-to-top   	{   margin-top:-5px;}\n\n\n[itemid] .showbiz-title,\n[itemid] .showbiz-title a,\n[itemid] .showbiz-title a:visited,\n[itemid] .showbiz-title a:hover               	{   font-weight:600; font-size:14px;\n                                                    text-transform:uppercase;\n                                                    font-family: ''Open Sans'', sans-serif;\n													color:#fff;\n												}\n													\n[itemid] .pt40                                  {   padding-top:40px;  }\n\n[itemid] .showbiz-title .greenbg			{	background-color:#366300; padding:2px 10px;}\n[itemid] .showbiz-title .redbg				{	background-color:#b2170a; padding:2px 10px;}\n[itemid] .showbiz-title .bluebg				{	background-color:#1d628a; padding:2px 10px;}\n[itemid] .showbiz-title .blackbg			{	background-color:#000; padding:2px 10px;}', 3, ''),
(4, 'ShowCase Skin', 'item', '<!-- SPECIAL CLASSES:\ngo-to-top -> Moves Caption to the Top Position after Hover,\ntofullwidth -> opens the Reveal (hidden container) to fullwidth\nreveal_wrapper -> The Hidden Content which is shown after click on openme,\nopenme, closeme -> The open , close icon in for show/hide the Hidden Content,\nreveal_opener -> The Wrapper for the open/close Buttons,\nshow_on_hover -> Show the reveal_opener only if mouse is hovering the teaser\nheightadjuster -> Reveal Container will fit with best height always\ntable -> display:table setting\ntable-cell -> display:table-cell setting\nonethird -> makes a 33% width of table cell\nhas been used. See Documentation for More Info\n-->\n\n<!-- THE MEDIA HOLDER HERE -->\n<div class="mediaholder ">\n	<div class="mediaholder_innerwrap">\n		<img alt="" src="[showbiz_image]">\n	</div>\n</div><!-- END OF MEDIA CONTAINER -->\n\n<!-- SOME ALWAYS VISIBLE CONTENT -->\n<div class="detailholder">\n  <h4 class="showbiz-title txt-center"><a href="[showbiz_link]">[showbiz_title]</a></h4>\n	<div class="divide5"></div>\n	<p class="txt-center">Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy.</p>\n</div><!-- END OF DEATIL CONTAINER -->\n\n<!-- THE REVEAL CONTAINER - OPENING IN FULLWIDTH -->\n<div class="reveal_container tofullwidth">\n\n	<!-- THE REVEL HIDDEN / VISIBLE CONTAINER -->\n	<div class="reveal_wrapper">\n		<!-- THE HEIGHT ADJUSTER CONTAINER -->\n		<div class="heightadjuster table">\n\n			<!-- TABLE CONSTRUCT FOR LEFT / RIGHT CONTENTS -->\n			<div class="table-cell onethird">\n					<img alt="" src="[showbiz_image]" width="100%">\n			</div>\n\n			<!-- CONTENT IN TABLE -->\n			<div class="table-cell pl20">\n              <h3 class="showbiz-title large"><a href="[showbiz_link]">[showbiz_title]</a></h3>\n				<div class="divide20"></div>\n				<p>[showbiz_excerpt]</p>\n				<div class="divide20"></div>\n				<p class="orange bigger bolder">Special Offer:</p>\n				<div class="showbiz-price">\n                  <span class="currency">$</span>\n                  <span class="number">[showbiz_price]</span>\n                </div>\n				<div class="divide20"></div>\n                <a class="showbiz-button">Purchase Now</a>\n			</div><!-- END OF CONTENT  -->\n\n		</div><!-- END OF HEIGHT ADJUSTER CONTAINER -->\n	</div><!-- END OF REVEAL HIDDEN/VISIBLE CONTAINER -->\n\n	<!-- THE CLOSER / OPENER FUNCTION -->\n	<a class="reveal_opener">\n		<span class="openme">+</span>\n		<span class="closeme">-</span>\n	</a><!-- END OF CLOSER / OPENER -->\n\n</div><!-- END OF THE REVEAL CONTAINER -->', '/**************************************\n\n	STYLE SETTINGS FOR SHOWCASE SKIN\n\n***************************************/\n\n[itemid]  ul li			        	{	margin-right:20px; position:relative; box-sizing:border-box; -moz-box-sizing:border-box;\n										background:#f5f5f5;}\n\n[itemid]  ul li .innerwrapper		{	border:1px solid #e8e8e8; padding-top:10px; box-sizing:border-box; -moz-box-sizing:border-box;}\n\n[itemid] .tofullwidth .reveal_wrapper {	background: #f5f5f5; border:1px solid #e8e8e8; }\n\n[itemid] .detailholder				{	padding:30px 20px;}\n\n[itemid] .mediaholder img			{	width:100%; padding:0px 10px;\n                                        box-sizing:border-box;\n                                        -moz-box-sizing:border-box;\n                                        -webkit-box-sizing:border-box}\n\n[itemid] .mediaholder_innerwrap		{	text-align: center;  }\n\n[itemid] .showbiz-title,\n[itemid] .showbiz-title a,\n[itemid] .showbiz-title a:visited,\n[itemid] .showbiz-title a:hover	   	{	color:#555; font-family: ''Open Sans'', sans-serif;\n                                        font-size:14px; text-transform:uppercase;\n                                        text-decoration: none; font-weight:700;}\n\n[itemid] .showbiz p					{	font-size:13px; line-height:22px; color:#777;\n                                                        font-family: ''Open Sans'', sans-serif; position:relative;}\n[itemid] .showbiz p.orange		  	{	color:#ff6c00;}\n[itemid] .showbiz p.bigger		   	{	font-size:17px; line-height:20px;}\n[itemid] .showbiz p.bolder	        {	font-weight:700;}\n[itemid] h4.showbiz-title           {  margin-bottom:0px !important;}\n[itemid] .showbiz-title.large,\n[itemid] .showbiz-title.large a,\n[itemid] .showbiz-title.large a:visited,\n[itemid] .showbiz-title.large a:hover	{	font-size:20px; line-height:25px; font-weight:800;margin-top:0px;}\n\n[itemid] .showbiz-price .number        	{	color:#555; font-family: ''Open Sans'', sans-serif; font-size:50px; font-weight: 800;}\n[itemid] .showbiz-price .currency	  	{	color:#555; font-family: ''Open Sans'', sans-serif; font-size:35px; font-weight: 400;}\n\n\n[itemid] .reveal_container				{	width: 20px;                                            \n                                            bottom: 0px;\n                                            right: 0px;\n  											top:auto;\n  											left:auto;\n  											-webkit-transition: all 0s ease-out;\n											-moz-transition: all 0s ease-out;\n											-o-transition: all 0s ease-out;\n											-ms-transition: all 0s ease-out;\n											transition-delay: 0.3s;\n											-webkit-transition-delay: 0.3s;\n											-moz-transition-delay: 0.3s;\n										}\n\n[itemid] .reveal_container.revactive	{	width: 100%;\n                                            height: 100%;\n                                            bottom: auto;\n                                            right: auto;\n  											top:0px;\n  											left:0px;\n  											transition-delay: 0s;\n											-webkit-transition-delay: 0s;\n											-moz-transition-delay: 0s;\n											-webkit-transition: all 0s ease-out;\n											-moz-transition: all 0s ease-out;\n											-o-transition: all 0s ease-out;\n											-ms-transition: all 0s ease-out;\n  											\n										}\n\n[itemid] .reveal_opener                 {   background-color:#e5e5e5;\n                                            border-radius:3px 0px 0px 0px;\n                                            -moz-border-radius:3px 0px 0px 0px;\n                                            -webkit-border-radius:3px 0px 0px 0px;\n										}\n[itemid] .reveal_opener .openme    		{ 	padding:6px;}\n[itemid] .reveal_opener .closeme    	{ 	padding:7px 9px;}', 4, ''),
(5, 'Vimeo Skin', 'item', '<!-- SPECIAL CLASSES:\ngo-to-top -> Moves Caption to the Top Position after Hover,\nreveal_wrapper -> The Hidden Content which is shown after click on openme,\nopenme, closeme -> The open , close icon in for show/hide the Hidden Content,\nreveal_opener -> The Wrapper for the open/close Buttons,\nshow_on_hover -> Show the reveal_opener only if mouse is hovering the teaser\nsb-vimeo-markup -> it creates the iframe for vimeo on Demand\nhas been used. See Documentation for More Info\n-->\n\n<!-- THE MEDIA HOLDER -->\n<div class="mediaholder">\n	<div class="mediaholder_innerwrap">\n		<img alt="" src="[showbiz_image]">\n	</div>\n</div><!-- END OF MEDIA HOLDER -->\n\n<!-- ANIMATED HEADING INFORMATION, ALWAYS VISIBLE -->\n<h4 class="showbiz-title go-to-top"><div class="[showbiz_color]"><a href="[showbiz_link]">[showbiz_title]</a></div></h4>\n\n<!-- REVEAL CONTAINER (SINGLE MODE) -->\n<div class="reveal_container">\n\n	<!-- THE REVEAL CONTENT, ONLY IN DETAIL MODE VISIBLE -->\n	<div class="reveal_wrapper">\n                <div class="sb-vimeo-markup" data-videoid="[showbiz_vimeo_id]"></div>\n	</div>\n\n</div><!-- END OF REVEAL CONTAINER -->\n\n<!-- THE REVEAL OPEN/CLOSE BUTTON - ONLY VISIBLE ON HOVER, DEFAULT STYLE -->\n<a class="reveal_opener show_on_hover sb-centered sb-controll-button">\n	<span class="openme"><i class="sb-icon-play"></i></span>\n	<span class="closeme"><i class="sb-icon-cancel"></i></span>\n</a>', '/**************************************\n\n	STYLE SETTINGS FOR VIMEO SKIN\n\n***************************************/\n\n\n[itemid] .showbiz-title,\n[itemid] .showbiz-title a,\n[itemid] .showbiz-title a:visited,\n[itemid] .showbiz-title a:hover   			{	color:#555; font-family: ''Open Sans'', sans-serif;\n                                                font-size:14px; text-transform:uppercase;\n                                                text-decoration: none; font-weight:700;\n                                            }\n\n[itemid] .showbiz p							{	font-size:13px; line-height:22px; color:#777;\n                                    	        font-family: ''Open Sans'', sans-serif; position:relative;\n                                        	}\n\n[itemid] .showbiz p.orange					{	color:#ff6c00;}\n[itemid] .showbiz p.bigger					{	font-size:17px; line-height:20px;}\n[itemid] .showbiz p.bolder					{	font-weight:700;}\n[itemid] h4.showbiz-title      				{  margin-bottom:0px !important;}\n\n[itemid] .showbiz-title,\n[itemid] .showbiz-title a,\n[itemid] .showbiz-title a:visited,\n[itemid] .showbiz-title a:hover				{	color:#fff}\n\n[itemid] .mediaholder 						{	background-color:#fff;border:none;padding:5px;\n                                                border-radius:2px;-moz-border-radius:2px;-webkit-border-radius:2px;\n                                            }\n\n[itemid] .reveal_opener .openme,\n[itemid] .reveal_opener .closeme  			{ background:#000;background:rgba(0,0,0,0.7);padding:4px 13px;border-radius:3px;-moz-border-radius:3px;-webkit-border-radius:3px; margin-top:-24px;margin-left:-24px;}\n[itemid] .reveal_opener .closeme			{ margin:10px 0px 0px 0px; padding:1px 10px; }\n\n[itemid] .reveal_opener.sb-controll-button { margin:0;top:50%;bottom:auto;left:50%;right:auto; overflow:visible; background:transparent; width:auto; height:auto;}\n[itemid] .reveal_opener.sb-controll-button.revactive { top: 0;bottom: auto;left: auto;right: 10px; }\n\n[itemid] .showbiz-title .greenbg			{	background-color:#366300; padding:2px 10px;}\n[itemid] .showbiz-title .redbg				{	background-color:#b2170a; padding:2px 10px;}\n[itemid] .showbiz-title .bluebg				{	background-color:#1d628a; padding:2px 10px;}\n[itemid] .showbiz-title .blackbg			{	background-color:#000; padding:2px 10px;}\n[itemid] .sb-vimeo-markup					{	height:100%;}\n[itemid] .fluid-width-video-wrapper			{	padding:0; height:100%; box-sizing:border-box; -webkit-box-sizing:border-box; -moz-box-sizing:border-box;}', 5, ''),
(6, 'Mixed Video Skin', 'item', '<!-- THE MEDIA HOLDER -->\n<div class="mediaholder">\n	<div class="mediaholder_innerwrap">\n      <div class="showbiz-videosrc">[showbiz_videosrc]</div>\n	</div>\n</div>\n<!-- END OF MEDIA HOLDER HERE -->\n\n\n<!-- THE VISIBLE CONTENT HOLDER ON THE TEASER-->\n<div class="detailholder">\n\n		<h4 class="showbiz-title txt-center"><a href="[showbiz_link]">[showbiz_title]</a></h4>\n		<div class="divide5"></div>\n		<p class="txt-center">[showbiz_excerpt]</p>\n</div>', '/**************************************\n\n	STYLE SETTINGS FOR LIGHT SKIN\n\n***************************************/\n\n[itemid].showbiz-container	{\n                            	max-width:1210px;\n                              	min-width:300px;\n                         	 }\n\n[itemid] .showbiz-title		{\n                              	margin-top:10px;\n                                text-align:center;\n                            }\n\n[itemid] .showbiz-videosrc	{	width:100%;min-height:100px;max-height:250px;height:100%; }\n\n[itemid] .showbiz-title,\n[itemid] .showbiz-title a,\n[itemid] .showbiz-title a:visited,\n[itemid] .showbiz-title a:hover	{\n                                color:#555;\n                                font-family: ''Open Sans'', sans-serif;\n                                font-size:14px;\n                                text-transform:uppercase;\n                                text-decoration: none;\n                                font-weight:700;\n                                        margin-bottom:0px !important;\n                        }\n\n[itemid] .showbiz-description{\n                                margin-top:10px;\n                                text-align:center;\n                                font-size:13px;\n                                line-height:22px;\n                                color:#777;\n                                font-family: ''Open Sans'', sans-serif;\n                            }\n\n[itemid]  ul li {\n                                margin-right:20px;                              \n\n                          }\n\n\n[itemid]  .detailholder	{\n                                background-color:#f5f5f5;\n                                padding:10px 20px 10px;\n                                border-radius:0px 0px 6px 6px;\n                                -moz-border-radius:0px 0px 6px 6px;\n                                -webkit-border-radius:0px 0px 6px 6px;\n								border-left: 1px solid #e8e8e8;\n								border-right: 1px solid #e8e8e8;\n								border-bottom: 1px solid #e8e8e8;\n                            }\n\n[itemid]  .mediaholder_innerwrap  {\n                                border-radius:6px 6px 0px 0px;\n                                -moz-border-radius:6px 6px 0px 0px;\n                                -webkit-border-radius:6px 6px 0px 0px;\n                                }\n\n[itemid]  .hovercover	{\n                                background:rgba(0,168,255,0.75);\n                                border-radius:6px 6px 0px 0px;\n                                -moz-border-radius:6px 6px 0px 0px;\n                                -webkit-border-radius:6px 6px 0px 0px;\n                      }\n\n[itemid]  .reveal_wrapper	{\n                                border-radius:6px;\n                                -moz-border-radius:6px;\n                                -webkit-border-radius:6px;\n                                }', 6, ''),
(7, 'YouTube Skin', 'item', '<!-- SPECIAL CLASSES:\ngo-to-top -> Moves Caption to the Top Position after Hover,\nreveal_wrapper -> The Hidden Content which is shown after click on openme,\nopenme, closeme -> The open , close icon in for show/hide the Hidden Content,\nreveal_opener -> The Wrapper for the open/close Buttons,\nshow_on_hover -> Show the reveal_opener only if mouse is hovering the teaser,\nsb-yt-markup -> Build YouTube Iframe on Demand in the Reveal (hidden) container\nhas been used. See Documentation for More Info\n-->\n\n<!-- THE MEDIA HOLDER -->\n<div class="mediaholder">\n	<div class="mediaholder_innerwrap">\n		<img alt="" src="[showbiz_image]">\n	</div>\n</div><!-- END OF MEDIA HOLDER -->\n\n<!-- ANIMATED HEADING INFORMATION, ALWAYS VISIBLE -->\n<h4 class="showbiz-title go-to-top"><div class="[showbiz_color]"><a href="[showbiz_link]">[showbiz_title]</a></div></h4>\n\n<!-- REVEAL CONTAINER (SINGLE MODE) -->\n<div class="reveal_container">\n\n	<!-- THE REVEAL CONTENT, ONLY IN DETAIL MODE VISIBLE -->\n	<div class="reveal_wrapper">\n		<div class="sb-yt-markup" data-videoid="[showbiz_youtube_id]"></div>\n	</div>\n\n</div><!-- END OF REVEAL CONTAINER -->\n\n<!-- THE REVEAL OPEN/CLOSE BUTTON - ONLY VISIBLE ON HOVER, DEFAULT STYLE -->\n<a class="reveal_opener show_on_hover sb-centered sb-controll-button">\n	<span class="openme"><i class="sb-icon-play"></i></span>\n	<span class="closeme"><i class="sb-icon-cancel"></i></span>\n</a>', '/**************************************\n\n	STYLE SETTINGS FOR YOUTUBE SKIN\n\n***************************************/\n\n\n[itemid] .showbiz-title,\n[itemid] .showbiz-title a,\n[itemid] .showbiz-title a:visited,\n[itemid] .showbiz-title a:hover   			{	color:#555; font-family: ''Open Sans'', sans-serif;\n                                                font-size:14px; text-transform:uppercase;\n                                                text-decoration: none; font-weight:700;\n                                            }\n\n[itemid] .showbiz p							{	font-size:13px; line-height:22px; color:#777;\n                                    	        font-family: ''Open Sans'', sans-serif; position:relative;\n                                        	}\n\n[itemid] .showbiz p.orange					{	color:#ff6c00;}\n[itemid] .showbiz p.bigger					{	font-size:17px; line-height:20px;}\n[itemid] .showbiz p.bolder					{	font-weight:700;}\n[itemid] h4.showbiz-title      				{  margin-bottom:0px !important;}\n\n[itemid] .showbiz-title,\n[itemid] .showbiz-title a,\n[itemid] .showbiz-title a:visited,\n[itemid] .showbiz-title a:hover				{	color:#fff}\n\n[itemid] .mediaholder 						{	background-color:#fff;border:none;padding:5px;\n                                                border-radius:2px;-moz-border-radius:2px;-webkit-border-radius:2px;\n                                            }\n\n[itemid] .reveal_opener .openme,\n[itemid] .reveal_opener .closeme  			{ background:#000;background:rgba(0,0,0,0.7);padding:4px 13px;border-radius:3px;-moz-border-radius:3px;-webkit-border-radius:3px; margin-top:-24px;margin-left:-24px;}\n[itemid] .reveal_opener .closeme			{ margin:10px 0px 0px 0px; padding:1px 10px; }\n\n[itemid] .reveal_opener.sb-controll-button { margin:0;top:50%;bottom:auto;left:50%;right:auto; overflow:visible; background:transparent; width:auto; height:auto;}\n[itemid] .reveal_opener.sb-controll-button.revactive { top: 0;bottom: auto;left: auto;right: 10px; }\n\n[itemid] .showbiz-title .greenbg			{	background-color:#366300; padding:2px 10px;}\n[itemid] .showbiz-title .redbg				{	background-color:#b2170a; padding:2px 10px;}\n[itemid] .showbiz-title .bluebg				{	background-color:#1d628a; padding:2px 10px;}\n[itemid] .showbiz-title .blackbg			{	background-color:#000; padding:2px 10px;}\n[itemid] .sb-yt-markup						{	height:100%;}\n[itemid] .fluid-width-video-wrapper			{	padding:0; height:100%; box-sizing:border-box; -webkit-box-sizing:border-box; -moz-box-sizing:border-box;}', 7, ''),
