                                        color: #999;\n												text-shadow: 1px 1px 0px #000;\n												background: #333;\n												border: 1px solid #151515;\n												text-decoration: none;\n												font-weight: 400;\n                                                white-space: nowrap;\n                                                font-size:12px;\n                                                text-decoration: none !important;\n                                             }\n\n[itemid]  .showbiz-navigation i				{	text-shadow:0px 1px 0px rgba(0,0,0,0.4); font-size:20px;}\n\n[itemid]  .mediaholder 						{	background-color:#fff;\n	                                            border:1px solid #111;\n	                                            border-radius:0px;-moz-border-radius:0px;-webkit-border-radius:0px;\n	                                            padding:0px;\n	                                        }\n\n[itemid] .hovercover						{	background:rgba(0,0,0,0.5);  }\n\n[itemid] li:hover .mediaholder img  		{	 filter: url("data:image/svg+xml;utf8,<svg xmlns=\\''http://www.w3.org/2000/svg\\''><filter id=\\''grayscale\\''><feColorMatrix type=\\''matrix\\'' values=\\''0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\\''/></filter></svg>#grayscale");\n                                           		 filter: gray; /* IE6-9 */\n                                           		 -webkit-filter: grayscale(100%);\n                                           	}\n\n\n[itemid]	.linkicon						{	 position:absolute; left:50%;top:50%;\n	                                            -webkit-transform: translateZ(10);\n	                                            -webkit-backface-visibility: hidden;\n	                                            -webkit-perspective: 1000;\n	                                            color:#fff; font-size:42px; font-weight:400 !important;\n\n	                                            margin-top:-23px; margin-left:-23px;\n	                                             background:#000; background:rgba(0,0,0,0.6);\n	                                             border-radius:50%;-moz-border-radius:50%;-webkit-border-radius:50%;\n\n	                                            vertical-align: middle !important;\n	                                            text-align: center;\n                                         }', 9, ''),
(10, 'Modern Skin', 'item', '<!-- THE MEDIA HOLDER -->\n<div class="mediaholder">\n  <div class="mediaholder_innerwrap">\n    <img alt="" src="[showbiz_image]">\n    </div>\n  </div><!-- END OF MEDIA HOLDER -->\n\n  <!-- A DARK BG WHICH IS COLORED ON HOVER ON THE TEASER -->\n  <div class="darkhover"></div>\n\n  <!-- THE DETAILS like Title, Author, News, Date etc.. -->\n  <div class="detailholder">\n    <a href="[showbiz_link]" class="showbiz-title">[showbiz_title]</a>\n    <div class="divide20"></div>\n\n    <!-- EXCERTP INFO, THIS IS HIDDEN WHEN NOT HOVERED !! -->\n    <p class="excerpt">\n      [showbiz_excerpt]\n      <div class="divide20"></div>\n    </p>\n\n    <!-- THE POST INFOS AND READ MORE BUTTON -->\n    <div class="sb-post-details leftfloat"><span class="rm15">[showbiz_alias]</span><span class="rm15">[showbiz_date]</span></div>\n    <div class="sb-readmore rightfloat"><a href="[showbiz_link]">READ MORE</a></div>\n    <div class="sb-clear"></div><!-- END OF POST INFOS AND READ MORE BUTTON -->\n  </div>', '/**************************************\n\n	STYLE SETTINGS FOR MODERN SKIN\n\n***************************************/\n\n[itemid] ul										{	background:#000;}\n[itemid] ul li									{	margin-right:0px; border-right:1px solid #000;}\n\n\n\n[itemid] .darkhover								{	position:absolute; top:0px; left:0px; width:100%;height:100%;\n													background:rgba(0,0,0,0);\n													-webkit-transition: all 0.2s ease-out;\n													-moz-transition: all 0.2s ease-out;\n													-o-transition: all 0.2s ease-out;\n													-ms-transition: all 0.2s ease-out;\n												}\n\n[itemid] .detailholder							{  position: absolute;\n													bottom: 0px;\n													background:#000;\n													background: rgba(0,0,0,0.65);\n													padding: 20px;\n													box-sizing: border-box; -moz-box-sizing: border-box; -webkit-box-sizing: border-box;\n													overflow: hidden;\n													width:100%;\n													-webkit-transition: all 0.2s ease-out;\n													-moz-transition: all 0.2s ease-out;\n													-o-transition: all 0.2s ease-out;\n													-ms-transition: all 0.2s ease-out;\n													-webkit-transform: translateZ(0px)\n												}\n\n[itemid] .showbiz-title,\n[itemid] a.showbiz-title,\n[itemid] a.showbiz-title:visited							{	font-size:20px; line-height:25px; color:#fff;  font-weight:400;}\n\n[itemid] .excerpt								{	font-size:13px; line-height:22px; color:#999;   font-weight:400;\n													overflow: hidden; position: relative;\n													display:none;\n												}\n\n.sb-post-details, .sb-post-details span,\n.sb-post-details a, .sb-post-details a:visited	{	font-size:12px; line-height:22px;\n													color:#999; font-weight:400;\n													font-family: ''Open Sans'', sans-serif;}\n\n.sb-readmore, .sb-readmore span,\n.sb-readmore a, .sb-readmore a:visited			{	font-size:12px; line-height:22px;\n													color:#fff; font-family: ''Open Sans'', sans-serif;\n													font-weight:500; text-decoration: none;}\n\n[itemid] .sb-readmore							{   -ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=0)";\n													  filter: alpha(opacity=0);\n													  -moz-opacity: 0.0;\n													  -khtml-opacity: 0.0;\n													  opacity: 0.0;\n													  -webkit-transition: all 0.2s ease-out;\n													  -moz-transition: all 0.2s ease-out;\n													  -o-transition: all 0.2s ease-out;\n													  -ms-transition: all 0.2s ease-out;\n												}\n\n\n[itemid] ul li:hover .detailholder		{	background:#000; background:rgba(0,0,0,1);}\n[itemid] ul li:hover .darkhover			{	background:rgba(0,0,0,0.65);}\n\n\n[itemid] ul li:hover .sb-readmore		{   -ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=100)";\n													filter: alpha(opacity=100);\n													-moz-opacity: 1;\n													-khtml-opacity: 1;\n													opacity: 1;\n												}\n      \n[itemid] .mediaholder {\n                            overflow: hidden;\n                            position: relative;\n                            -webkit-border-radius: 0;\n                            -moz-border-radius: 0;\n                            border-radius: 0;\n                            \n                            }      ', 10, ''),
(11, 'WooCommerce Skin', 'item', '<!-- THE MEDIAHOLDER CONTAINER -->\n<div class="mediaholder">\n  <!-- THE INNER WRAP FOR MEDIA HOLDER -->\n  <div class="mediaholder_innerwrap">\n    <img alt="" src="[showbiz_image]">\n    \n    <!-- THE HOVER CONTAINER AND THE HOVER ICONS -->\n    <div class="hovercover" >\n      <a href="[showbiz_link]"><div class="linkicon notalone"><i class="icon-link"></i></div></a>\n      <a class="fancybox" rel="group" href="[showbiz_image]"><div class="lupeicon notalone"><i class="icon-search"></i></div></a>\n    </div><!-- END OF HOVER CONTIANER -->\n    \n  </div><!-- END OF MEDIAHOLDER INNER WRAP-->\n</div><!-- END OF MEDIAHOLDER CONTAINER -->\n\n<!-- THE VISIBLE DETAILS -->\n<div class="detailholder">\n  <div class="divide15"></div>\n  <div class="showbiz-title txt-center"><a href="[showbiz_link]">[showbiz_title]</a></div>\n  <div class="divide5"></div>\n  <p class="txt-center">[showbiz_excerpt]</p>\n  <div class="divide10"></div>\n  <div class="txt-center"><span class="rating-star" data-rates="[showbiz_wc_rating]"></span></div>\n  <div class="txt-center"><span class="rating-text">RATED [showbiz_wc_rating] OUT OF 5</span></div>\n  <div class="txt-center"><span class="rating-text">[showbiz_wc_categories]</span></div>\n  <hr>\n  [add_to_cart id="[showbiz_id]"]\n</div><!-- END OF VISIBLE DETAILS -->', '/********************************************\n\n	STYLE SETTINGS FOR THE WOO SKIN\n\n*********************************************/\n\n\n[itemid] li							{	margin-right:20px;  }\n\n[itemid]  .mediaholder 				{	background-color:#d5d5d5;border:1px solid #b9b9b9;										\n										border-radius:2px;-moz-border-radius:2px;-webkit-border-radius:2px;\n										padding:7px;\n									}\n\n[itemid]  .hovercover				{	background:rgba(0,0,0,0.5);  }\n\n\n[itemid]  .rating-text				{	font-family: ''Open Sans'', sans-serif; font-size:11px; color:#999; line-height:25px;}\n[itemid]  .rating-stars				{	width:69px;height:13px;margin-right:10px; vertical-align: middle; margin-top:-2px;}\n[itemid]  hr						{	border:none; border-top:1px solid #d5d5d5;margin-top:4px;margin-bottom:8px;}\n\n[itemid]  .sb-price,\n[itemid] .amount,\n[itemid] .product.woocommerce span  {	line-height: 26px;vertical-align: top;\n										padding-top:0;font-size:22px; font-weight:700; \n										margin-right:15px; color:#b1749d; font-family: ''Open Sans'', sans-serif;\n									}\n\n[itemid]  .showbiz-button,\n[itemid]  .add_to_cart_button,\n	{\n				                            padding:4px 15px !important;\n				                            color:#777 !important;\n				                            text-shadow:1px 1px 0px #fff;\n				                            font-size:12px; line-height:27px;\n				                            margin-top:0;\n				                            white-space: nowrap;\n				                            text-decoration: none !important;\n				                            vertical-align: middle;\n				                     }\n\n[itemid] .product.woocommerce		{   border:none !important; padding:0 !important; text-align: center !important;}\n[itemid] .product.woocommerce small	{	line-height: 26px;vertical-align: top;\n										padding-top:0;font-size:16px; font-weight:700; \n										margin-right:15px; color:#b1749d; font-family: ''Open Sans'', sans-serif;\n  										text-decoration: line-through;\n									}', 11, '');
INSERT INTO `wp_showbiz_templates` (`id`, `title`, `type`, `content`, `css`, `ordering`, `params`) VALUES
(12, 'WooCommerce ShowCase Skin', 'item', '<!-- SPECIAL CLASSES:\ngo-to-top -> Moves Caption to the Top Position after Hover,\ntofullwidth -> opens the Reveal (hidden container) to fullwidth\nreveal_wrapper -> The Hidden Content which is shown after click on openme,\nopenme, closeme -> The open , close icon in for show/hide the Hidden Content,\nreveal_opener -> The Wrapper for the open/close Buttons,\nshow_on_hover -> Show the reveal_opener only if mouse is hovering the teaser\nheightadjuster -> Reveal Container will fit with best height always\ntable -> display:table setting\ntable-cell -> display:table-cell setting\nonethird -> makes a 33% width of table cell\nhas been used. See Documentation for More Info\n-->\n<div class="innerwrapper">\n    <!-- THE MEDIA HOLDER HERE -->\n    <div class="mediaholder ">\n        <div class="mediaholder_innerwrap">\n            <img alt="" src="[showbiz_image]">\n        </div>\n    </div><!-- END OF MEDIA CONTAINER -->\n    \n    <!-- SOME ALWAYS VISIBLE CONTENT -->\n    <div class="detailholder">\n      <h4 class="showbiz-title txt-center"><a href="[showbiz_link]">[showbiz_title]</a></h4>\n        <div class="divide5"></div>\n        <p class="txt-center">Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy.</p>\n    </div><!-- END OF DEATIL CONTAINER -->\n    \n    <!-- THE REVEAL CONTAINER - OPENING IN FULLWIDTH -->\n    <div class="reveal_container tofullwidth">\n    \n        <!-- THE REVEL HIDDEN / VISIBLE CONTAINER -->\n        <div class="reveal_wrapper">\n            <!-- THE HEIGHT ADJUSTER CONTAINER -->\n            <div class="heightadjuster table">\n    \n                <!-- TABLE CONSTRUCT FOR LEFT / RIGHT CONTENTS -->\n                <div class="table-cell onethird">\n                        <img alt="" src="[showbiz_image]" width="100%">\n                </div>\n    \n                <!-- CONTENT IN TABLE -->\n                <div class="table-cell pl20">\n                  <h3 class="showbiz-title large"><a href="[showbiz_link]">[showbiz_title]</a></h3>\n                    <div class="divide20"></div>\n                    <p>[showbiz_excerpt]</p>\n                    <div class="divide20"></div>\n                    <p class="lila bigger bolder">Special Offer:</p>\n                     [add_to_cart id="[showbiz_id]"]\n                    <div class=""><span class="rating-star" data-rates="[showbiz_wc_rating]"></span></div>\n                    <div class=""><span class="rating-text">RATED [showbiz_wc_rating] OUT OF 5</span></div>\n                    <div class=""><span class="rating-text">[showbiz_wc_categories]</span></div>\n                </div><!-- END OF CONTENT  -->\n    \n            </div><!-- END OF HEIGHT ADJUSTER CONTAINER -->\n        </div><!-- END OF REVEAL HIDDEN/VISIBLE CONTAINER -->\n    \n        <!-- THE CLOSER / OPENER FUNCTION -->\n        <a class="reveal_opener">\n            <span class="openme">+</span>\n            <span class="closeme">-</span>\n        </a><!-- END OF CLOSER / OPENER -->\n    \n    </div><!-- END OF THE REVEAL CONTAINER -->\n</div>', '/**************************************\n\n	STYLE SETTINGS FOR SHOWCASE SKIN\n\n***************************************/\n\n[itemid]  ul li			        	{	margin-right:20px; position:relative; box-sizing:border-box; -moz-box-sizing:border-box;\n										background:#f5f5f5;}\n[itemid]  ul li .innerwrapper		{	border:1px solid #e8e8e8; padding-top:10px; box-sizing:border-box; -moz-box-sizing:border-box;}\n[itemid] .tofullwidth .reveal_wrapper {	background: #f5f5f5; border:1px solid #e8e8e8; }\n\n[itemid] .detailholder				{	padding:30px 20px;}\n\n[itemid] .mediaholder img			{	width:100%; padding:0px 10px;\n                                        box-sizing:border-box;\n                                        -moz-box-sizing:border-box;\n                                        -webkit-box-sizing:border-box}\n\n[itemid] .mediaholder_innerwrap		{	text-align: center;  }\n\n[itemid] .showbiz-title,\n[itemid] .showbiz-title a,\n[itemid] .showbiz-title a:visited,\n[itemid] .showbiz-title a:hover	   	{	color:#555; font-family: ''Open Sans'', sans-serif;\n                                        font-size:14px; text-transform:uppercase;\n                                        text-decoration: none; font-weight:700;}\n\n[itemid] .showbiz p					{	font-size:13px; line-height:22px; color:#777;\n                                                        font-family: ''Open Sans'', sans-serif; position:relative;}\n[itemid] .showbiz p.lila		  	{	color:#b1749d;}\n[itemid] .showbiz p.bigger		   	{	font-size:17px; line-height:20px;}\n[itemid] .showbiz p.bolder	        {	font-weight:700;}\n[itemid] h4.showbiz-title           {  margin-bottom:0px !important;}\n[itemid] .showbiz-title.large,\n[itemid] .showbiz-title.large a,\n[itemid] .showbiz-title.large a:visited,\n[itemid] .showbiz-title.large a:hover	{	font-size:20px; line-height:25px; font-weight:800;margin-top:0px;}\n\n[itemid] .showbiz-price .number        	{	color:#555; font-family: ''Open Sans'', sans-serif; font-size:50px; font-weight: 800;}\n[itemid] .showbiz-price .currency	  	{	color:#555; font-family: ''Open Sans'', sans-serif; font-size:35px; font-weight: 400;}\n\n\n[itemid] .reveal_container				{	width: 20px;                                            \n                                            bottom: 0px;\n                                            right: 0px;\n  											top:auto;\n  											left:auto;\n  											-webkit-transition: all 0s ease-out;\n											-moz-transition: all 0s ease-out;\n											-o-transition: all 0s ease-out;\n											-ms-transition: all 0s ease-out;\n											transition-delay: 0.3s;\n											-webkit-transition-delay: 0.3s;\n											-moz-transition-delay: 0.3s;\n										}\n\n[itemid] .reveal_container.revactive	{	width: 100%;\n                                            height: 100%;\n                                            bottom: auto;\n                                            right: auto;\n  											top:0px;\n  											left:0px;\n  											transition-delay: 0s;\n											-webkit-transition-delay: 0s;\n											-moz-transition-delay: 0s;\n											-webkit-transition: all 0s ease-out;\n											-moz-transition: all 0s ease-out;\n											-o-transition: all 0s ease-out;\n											-ms-transition: all 0s ease-out;\n  											\n										}\n\n[itemid] .reveal_opener                 {   background-color:#e5e5e5;\n                                            border-radius:3px 0px 0px 0px;\n                                            -moz-border-radius:3px 0px 0px 0px;\n                                            -webkit-border-radius:3px 0px 0px 0px;\n										}\n[itemid] .reveal_opener .openme    		{ 	padding:6px;}\n[itemid] .reveal_opener .closeme    	{ 	padding:7px 9px;}\n\n[itemid]  .rating-stars				{	width:69px;height:13px;margin-right:10px; vertical-align: middle; margin-top:-2px;}\n[itemid]  hr						{	border:none; border-top:1px solid #d5d5d5;margin-top:4px;margin-bottom:8px;}\n\n[itemid]  .sb-price,\n[itemid] .amount,\n[itemid] .product.woocommerce span  {	line-height: 26px;vertical-align: top;\n										padding-top:0;font-size:22px; font-weight:700; \n										margin-right:15px; color:#b1749d; font-family: ''Open Sans'', sans-serif;\n									}\n\n[itemid]  .showbiz-button,\n[itemid]  .add_to_cart_button,\n	{\n				                            padding:4px 15px !important;\n				                            color:#777 !important;\n				                            text-shadow:1px 1px 0px #fff;\n				                            font-size:12px; line-height:27px;\n				                            margin-top:0;\n				                            white-space: nowrap;\n				                            text-decoration: none !important;\n				                            vertical-align: middle;\n				                     }\n\n[itemid] .product.woocommerce		{   border:none !important; padding:0 !important;}\n[itemid] .product.woocommerce small	{	line-height: 26px;vertical-align: top;\n										padding-top:0;font-size:16px; font-weight:700; \n										margin-right:15px; color:#b1749d; font-family: ''Open Sans'', sans-serif;\n  										text-decoration: line-through;\n									}', 12, ''),
(13, 'Velocity Blog Skin', 'item', '<!-- THE MEDIA HOLDER PART -->\n<div class="mediaholder">\n	<div class="mediaholder_innerwrap">\n		<img alt="image" src="[showbiz_image]">\n\n		<!-- THE HOVER EFFECT (SPECIAL CLASS  - hovercover) -->\n        <div class="hovercover">\n\n			<!-- LINK TO THE POST -->\n			<a href="[showbiz_link]">\n				<div class="linkicon notalone"><i class="sb-icon-link"></i></div>\n			</a>\n			<!-- LIGHTBOX LINK -->\n			<a class="fancybox" rel="group" href="[showbiz_image]"><div class="lupeicon notalone"><i class="sb-icon-search"></i></div></a>\n		</div>	<!-- END OF THE HOVER EFFECT -->\n      </div>\n</div><!-- END OF MEDIA HOLDER PART -->\n\n<!-- VISIBLE TEXTS ON THE TEASER - TITLE AND EXCERPT -->\n<div class="detailholder">\n	<div class="showbiz-title"><a href="[showbiz_link]">[showbiz_title]</a></div>\n	<div class="showbiz-description">[showbiz_catlist]</div>\n</div>', '/********************************************\n\n	STYLE SETTINGS FOR THE VELOCITY BLOG SKIN\n\n*********************************************/\n\n[itemid].showbiz-container{\n	max-width:1170px;\n	min-width:300px;\n}\n\n[itemid] .showbiz-title				{\n                                        margin-top:10px;\n                                        text-align:center;\n                                    }\n\n[itemid] .showbiz-title,\n[itemid] .showbiz-title a,\n[itemid] .showbiz-title a:visited,\n[itemid] .showbiz-title a:hover		{\n                                            color:#111;\n                                            font-family: ''Open Sans'', sans-serif;\n                                            font-size:18px;\n 											line-height: 22px;\n                                            text-transform:none;\n                                            text-decoration: none;\n                                            font-weight:300;\n                                    }\n\n[itemid] .showbiz-description		{\n                                        margin-top:7px;\n                                        text-align:center;\n                                        font-size:13px;\n                                        line-height:22px;\n                                        color:#aaa;\n                                        font-family: ''Open Sans'', sans-serif;\n\n                                    }\n[itemid] .showbiz-description a, \n[itemid] .showbiz-description a:visited	{ color: #aaa; }\n \n[itemid] .showbiz-description a:hover	{ color: #aaa; }\n\n[itemid]  .detailholder				{   border: 1px solid #e5e5e5;\n  border-top: 0;\n-webkit-border-bottom-right-radius: 5px;\n-webkit-border-bottom-left-radius: 5px;\n-moz-border-radius-bottomright: 5px;\n-moz-border-radius-bottomleft: 5px;\nborder-bottom-right-radius: 5px;\nborder-bottom-left-radius: 5px;\n  padding: 20px; \n  padding-top: 11px;\n  padding-bottom: 19px;\n  box-sizing:border-box; -moz-box-sizing:border-box; -webkit-box-sizing:border-box;\n}\n\n[itemid]  .mediaholder 				{	\n                                      -webkit-border-radius: 0;\n-moz-border-radius: 0;\nborder-radius: 0;\n                                       -webkit-border-top-left-radius: 5px;\n-webkit-border-top-right-radius: 5px;\n-moz-border-radius-topleft: 5px;\n-moz-border-radius-topright: 5px;\nborder-top-left-radius: 5px;\nborder-top-right-radius: 5px;\n                                       \n                                    }\n\n[itemid]  .hovercover				{	background:rgba(0,0,0,0.5);  }\n\n[itemid] li:hover .mediaholder img  {	\n                                    }', 13, ''),
(14, 'Velocity Portfolio Skin', 'item', '<!-- THE MEDIA HOLDER -->\n<div class="mediaholder">\n  <div class="mediaholder_innerwrap">\n    <img alt="Showcase Image" src="[showbiz_image]">\n    </div>\n  </div><!-- END OF MEDIA HOLDER -->\n\n  <div class="darkhover"></div>\n\n  <!-- THE DETAILS like Title, Author, News, Date etc.. -->\n  <div class="detailholder">\n    <a href="[showbiz_link]" class="showbiz-title">[showbiz_title]</a>\n    <div class="sb-moreholder"><a href="[showbiz_link]" class="btn-arrow"></a></div>\n  </div>', '/**************************************\n\n	STYLE SETTINGS FOR VELOCITY SHOWCASE / PORTFOLIO SKIN\n\n***************************************/\n\n[itemid]										{	background:#333637;}\n[itemid] ul										{	background:#333637;}\n[itemid] ul li									{	margin-right:0px; border-right:0;}\n[itemid] .darkhover								{	position:absolute; top:0px; left:0px; width:100%;height:100%;\n													background:rgba(0,0,0,0);\n													-webkit-transition: all 0.2s ease-out;\n													-moz-transition: all 0.2s ease-out;\n													-o-transition: all 0.2s ease-out;\n													-ms-transition: all 0.2s ease-out;\n													z-index: 99;\n												}\n[itemid] .detailholder							{  position: absolute;\n													bottom: 0px;\n													background:#000;\n													background: rgba(0,0,0,0.65);\n													padding: 20px;\n													box-sizing: border-box; -moz-box-sizing: border-box; -webkit-box-sizing: border-box;\n													overflow: hidden;\n													width:100%;\n													-webkit-transition: all 0.2s ease-out;\n													-moz-transition: all 0.2s ease-out;\n													-o-transition: all 0.2s ease-out;\n													-ms-transition: all 0.2s ease-out;\n													-webkit-transform: translateZ(0px);\n													z-index: 100;\n  													-ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=0)";\n													filter: alpha(opacity=0);\n													-moz-opacity: 0.0;\n													-khtml-opacity: 0.0;\n													opacity: 0.0;\n												}\n\n[itemid] .showbiz-title,\n[itemid] a.showbiz-title,\n[itemid] a.showbiz-title:visited				{	float: left; width: 80%; text-transform: none; font-size:18px; line-height:22px; color:#fff; font-weight:700;}\n\n[itemid] ul li:hover .detailholder				{	background:#000; background:rgba(0,0,0,0.65); \n     												-ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=100)";\n													filter: alpha(opacity=100);\n													-moz-opacity: 1;\n													-khtml-opacity: 1;\n													opacity: 1;}\n[itemid] ul li:hover .darkhover					{	\n													background:rgba(0,0,0,0.25);\n												}\n\n[itemid] .sb-moreholder							{	float: right; position: absolute; right:20px; top: 50%; margin-top: -15px; }\n\n[itemid] .mediaholder {\n                            overflow: hidden;\n                            position: relative;\n                            -webkit-border-radius: 0;\n                            -moz-border-radius: 0;\n                            border-radius: 0;\n                            }', 14, ''),
(15, 'WooCommerce Velocity Skin', 'item', '<!-- THE MEDIA HOLDER -->\n<div class="mediaholder">\n  <div class="mediaholder_innerwrap">\n    <img alt="Showcase Image" src="[showbiz_image]">\n    </div>\n  </div><!-- END OF MEDIA HOLDER -->\n\n  <div class="darkhover"></div>\n\n  <!-- THE DETAILS like Title, Author, News, Date etc.. -->\n  <div class="detailholder">\n    <a href="[showbiz_link]" class="showbiz-title">[showbiz_title] - $[showbiz_wc_regular_price]</a>\n    <div class="sb-moreholder"><a href="[showbiz_link]" class="btn-arrow"></a></div>\n  </div>', '/**************************************\n\n	STYLE SETTINGS FOR MODERN SKIN\n\n***************************************/\n\n[itemid]										{	background:#e5e5e5;}\n[itemid] ul										{	background:#e5e5e5;}\n[itemid] ul li									{	margin-right:1px; border-right:0;}\n[itemid] .darkhover								{	position:absolute; top:0px; left:0px; width:100%;height:100%;\n													background:rgba(0,0,0,0);\n													-webkit-transition: all 0.2s ease-out;\n													-moz-transition: all 0.2s ease-out;\n													-o-transition: all 0.2s ease-out;\n													-ms-transition: all 0.2s ease-out;\n													z-index: 99;\n												}\n[itemid] .detailholder							{  position: absolute;\n													bottom: 0px;\n													background:#000;\n													background: rgba(0,0,0,0.65);\n													padding: 20px;\n													box-sizing: border-box; -moz-box-sizing: border-box; -webkit-box-sizing: border-box;\n													overflow: hidden;\n													width:100%;\n													-webkit-transition: all 0.2s ease-out;\n													-moz-transition: all 0.2s ease-out;\n													-o-transition: all 0.2s ease-out;\n													-ms-transition: all 0.2s ease-out;\n													-webkit-transform: translateZ(0px);\n													z-index: 100;\n												}\n\n[itemid] .showbiz-title,\n[itemid] a.showbiz-title,\n[itemid] a.showbiz-title:visited				{	float: left; width: 80%; text-transform: none; font-size:18px; line-height:22px; color:#fff; font-weight:700;}\n\n[itemid] ul li:hover .detailholder				{	background:#000; background:rgba(0,0,0,0.65); }\n     												\n[itemid] ul li:hover .darkhover					{	\n													background:rgba(0,0,0,0.1);\n												}\n\n[itemid] .sb-moreholder							{	float: right; position: absolute; right:20px; top: 50%; margin-top: -15px; }\n[itemid] .sb-priceholder						{ color: #fff; float: right; position: absolute; right:100px; top: 50%; margin-top: -15px;}\n[itemid] .mediaholder {\n                            overflow: hidden;\n                            position: relative;\n                            -webkit-border-radius: 0;\n                            -moz-border-radius: 0;\n                            border-radius: 0;\n                            }', 15, ''),
(16, 'WooCommerce Velocity Skin 2', 'item', '<!-- THE MEDIA HOLDER -->\n<div class="mediaholder">\n  \n  <div class="sb-priceholder">[showbiz_wc_regular_price] $</div>\n  \n  <div class="mediaholder_innerwrap">\n    <img alt="Showcase Image" src="[showbiz_image]">\n  </div>\n  </div><!-- END OF MEDIA HOLDER -->\n\n  <div class="darkhover"></div>\n\n  <!-- THE DETAILS like Title, Author, News, Date etc.. -->\n  <div class="detailholder">\n    <a href="[showbiz_link]" class="showbiz-title">[showbiz_title]</a>\n  </div>', '/**************************************\n\n	STYLE SETTINGS FOR MODERN SKIN\n\n***************************************/\n\n[itemid]										{	background:#e5e5e5; border: 1px solid #e5e5e5; }\n[itemid] ul										{	background:#e5e5e5;}\n[itemid] ul li									{	margin-right:1px; border-right:0;}\n[itemid] .darkhover								{	position:absolute; top:0px; left:0px; width:100%;height:100%;\n													background:rgba(0,0,0,0);\n													-webkit-transition: all 0.2s ease-out;\n													-moz-transition: all 0.2s ease-out;\n													-o-transition: all 0.2s ease-out;\n													-ms-transition: all 0.2s ease-out;\n													z-index: 99;\n												}\n[itemid] .detailholder							{  position: absolute;\n													bottom: 0px;\n													background:#000; \n  													background:rgba(0,0,0,0.65);\n													padding: 7px 10px;\n													box-sizing: border-box; -moz-box-sizing: border-box; -webkit-box-sizing: border-box;\n													overflow: hidden;\n													width:100%;\n													-webkit-transition: all 0.2s ease-out;\n													-moz-transition: all 0.2s ease-out;\n													-o-transition: all 0.2s ease-out;\n													-ms-transition: all 0.2s ease-out;\n													-webkit-transform: translateZ(0px);\n													z-index: 100;\n												}\n\n[itemid] .showbiz-title,\n[itemid] a.showbiz-title,\n[itemid] a.showbiz-title:visited				{	float: left; width: 80%; text-transform: none; font-size:13px; line-height:22px; color:#fff; font-weight:700;}\n\n[itemid] ul li:hover .detailholder				{	background:#000; \n  													background:rgba(0,0,0,0.65); }\n     												\n[itemid] ul li:hover .darkhover					{	\n													background:rgba(0,0,0,0.1);\n												}\n\n[itemid] .sb-moreholder							{	float: right; position: absolute; right:20px; top: 50%; margin-top: -15px; }\n[itemid] .sb-priceholder						{   color: #fff; position: absolute; right: 10px; top: 10px; min-height: 40px;\nmin-width: 40px; padding: 4px; font-size: 14px; line-height: 40px; font-weight: 700; text-align: center; -webkit-border-radius: 50%; -moz-border-radius: 50%; border-radius: 50%; background: #ad74a2; z-index: 10; }\n[itemid] .mediaholder {\n                            overflow: hidden;\n                            position: relative;\n                            -webkit-border-radius: 0;\n                            -moz-border-radius: 0;\n                            border-radius: 0;\n                            }', 16, ''),
